customStyle:
.counties.selected {
      stroke: #a2677d;
      fill-opacity: 0.85;
      stroke-width: 6px;
  }

  .counties {
    stroke: #fff;
    stroke-width: 2px;
  }

  .counties.metro {
    fill:#bdbdbd;
  }

  .counties.nonmetro {
    fill:#67a28c;
  }

  svg {
      max-width:100%;
      height:auto;
  }
